import R from 'ramda'
import request from 'superagent'
import $ from 'jquery'


export const fetchInformations = async () => {
  const {body} = await request.get(
    'http://robogit.org:8080/api/information'
  );
  for (let i = 0; i < body.length; ++i) {
    body[i] = body[i].information
  }
  return body;
};

export const loadMoreInformations = async ({offset}) => {
  $.ajax({
    url: "http://robogit.org:8080/api/information"
  }).then(function (data) {
    console.log("data = " + data);
    return data;
  });
};

export const fetchInformationById = async (id) => {
  $.ajax({
    url: "http://robogit.org:8080/api/information/" + id
  }).then(function (data) {
    console.log("data = " + data);
    return data
  });
};

export const fetchCategories = async () => {
  return [{name: 'Все'}, {name: 'Контроллеры'}, {name: 'Сенсоры'}, {name: 'Моторы'}, {name: 'Механические детали'}, {name: 'Другое'}]
};
