import React, {Component} from 'react'
import {Link} from 'react-router'
import {connect} from 'react-redux'

import {getTotalBasketCount, getTotalBasketPrice} from 'selectors'

import {
    fetchBasket
} from 'actions'

class BasketCart extends Component {
    componentDidMount() {
        this.props.fetchBasket();
    }

    render() {
        const {totalBasketCount, totalPrice} = this.props;
        return (<div className='cart'>
            <div className='dropdown'>
                <Link
                    to='/basket'
                    id='dLabel'
                    className='btn btn-inverse btn-block btn-lg'
                >
                    <i className='fa fa-fa-shopping-cart'/>
                    <span>{totalBasketCount} товар(ов) - ${totalPrice}</span>
                </Link>
            </div>
        </div>);
    }
}

const mapStateToProps = state => {
    return {
        totalBasketCount: getTotalBasketCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
};

const mapDispatchToProps = {
    fetchBasket
};

export default connect(mapStateToProps, mapDispatchToProps)(BasketCart)