import React from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import {Link} from 'react-router'

import {
  getTotalBasketPrice,
  getBasketInformationsWithCount,
} from 'selectors'

import {
  removeInformationFromBasket,
  basketCheckout,
  cleanBasket
} from 'actions'

const Basket = ({
  informations,
  totalPrice,
  basketCheckout,
  removeInformationFromBasket,
  cleanBasket
}) => {
  const isBasketEmpty = R.isEmpty(informations)

  const renderContent = () => {
    return (
      <div>
        {isBasketEmpty && <div>Ваша корзина пуста.</div>}

        <div className='table-responsive'>
          <table className='table-bordered table-striped table-condensed cf'>
            <tbody>
            {informations.map((information, index) => (
              <tr
                key={index}
                className='item-checout'
              >
                <td className='first-column-checkout'>
                  <img
                    className='img-thumbnail'
                    src={information.image}
                    alt={information.name}
                  />
                </td>
                <td>{information.name}</td>
                <td>${information.price}</td>
                <td>{information.count}</td>
                <td>
                    <span
                      className='delete-cart'
                      onClick={() => removeInformationFromBasket(information.id)}
                    />
                </td>
              </tr>
            ))}
            </tbody>
          </table>
        </div>
        {
          R.not(isBasketEmpty) &&
          <div className='row'>
            <div className='pull-right total-user-checkout'>
              <b>Total:</b>
              ${totalPrice}
            </div>
          </div>
        }
      </div>
    )
  }

  const renderSidebar = () => (
    <div>
      <Link
        className='btn btn-info'
        to='/'
      >
        <span className='glyphicon glyphicon-info-sign'/>
        <span>Продолжить покупки!</span>
      </Link>
      {
        R.not(isBasketEmpty) &&
        <div>
          <button
            onClick={cleanBasket}
            className='btn btn-danger'
          >
            <span className='glyphicon glyphicon-trash' />
            Очистить корзину
          </button>
          <button
            className='btn btn-success'
            onClick={() => basketCheckout(informations)}
          >
            <span className='glyphicon glyphicon-envelope' />
            Checkout
          </button>
        </div>
      }
    </div>
  )


  return (
    <div className='view-container'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-9'>
            {renderContent()}
          </div>
          <div className='col-md-3 btn-user-checkout'>
            {renderSidebar()}
          </div>
        </div>
      </div>
    </div>
  )

}

const mapStateToProps = state => {
  return {
    informations: getBasketInformationsWithCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

const mapDispatchToProps = {
  basketCheckout,
  removeInformationFromBasket,
  cleanBasket

}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)

