import React, {Component} from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import {Link} from 'react-router'

import {
  fetchInformations,
  loadMoreInformations,
  addInformationToBasket,
  fetchCategories
} from 'actions'
import {getInformations} from 'selectors'

class Informations extends Component {
  componentDidMount () {
    this.props.fetchInformations()
    this.props.fetchCategories()
  }

  renderInformation (information, index) {
    const {addInformationToBasket} = this.props
    // const shortDescription = `${R.take(60, information.description)}...`
    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <img
            className='img-thumbnail'
            src="/uploads/b_four.png"
            alt={information.name}
          />
          <div className='caption'>
            <h4 className='pull-right'>${information.price}</h4>
            <h4>
              <Link to={`/information/${information.id}`}>
                {information.name}
              </Link>
            </h4>
            {/*<p>{shorstDescription}</p>*/}
            <p className='itemButton'>
              <button
                className='btn btn-primary'
                onClick={() => addInformationToBasket(information.id)}
              >
                В корзину!
              </button>
              <Link
                to={`/information/${information.id}`}
                className='btn btn-default'
              >
                Информация
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }


  render () {
    const {informations, loadMoreInformations} = this.props
    return (
      <div>
        <div className='books row'>
          {informations.map((information, index) => this.renderInformation(information, index))}
        </div>
        <div className='row'>
          <div className='col-md-12'>
            <button
              onClick={loadMoreInformations}
              className='pull-right btn btn-primary'
            >
              Больше
            </button>
          </div>
        </div>
      </div>

    )
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    informations: getInformations(state, ownProps)
  }
}

const mapDispatchToProps = {
  fetchInformations,
  loadMoreInformations,
  addInformationToBasket,
  fetchCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(Informations)
