import R from 'ramda'
import $ from 'jquery'

import {
    ADD_INFORMATION_TO_BASKET,
    REMOVE_INFORMATION_FROM_BASKET,
    CLEAN_BASKET,
    FETCH_BASKET_SUCCESS,
    BASKET_TO_ORDER_FAILURE,
    BASKET_TO_ORDER
} from 'actionTypes'

import {getInformationById} from 'selectors'

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_BASKET_SUCCESS:
            return payload;
        case ADD_INFORMATION_TO_BASKET:
            // if (getInformationById(state, payload).amount === 1){
            // }
            const data = {
                partId: payload,
                amount: 1
            };
            $.ajax({
                type: 'POST',
                url: 'http://robogit.org:8080/api/card/add',
                data: data
            });
            return R.append(payload, state);
        case REMOVE_INFORMATION_FROM_BASKET:
            const data_ = {
                partId: payload
            };
            $.ajax({
                type: 'POST',
                url: 'http://robogit.org:8080/api/card/remove',
                data: data_
            });
            return R.without(R.of(payload), state);
        case BASKET_TO_ORDER_FAILURE:
            alert("Упс, какой-то    товар  закончился на складе." ); //FIXME
            return state;
        case BASKET_TO_ORDER:
            alert("Заказ успешно оформлен. Номер Вашего заказа: " + payload + "\n"
                + "Для оформления заказа воспользуйтесь ботом t.me/id_bot"); //FIXME
        case CLEAN_BASKET:
            $.ajax({
                type: 'POST',
                url: 'http://robogit.org:8080/api/card/clean',
            });
            return [];
        default:
            return state;
    }
}
