import R from 'ramda'
import $ from 'jquery'

import {
    ADD_INFORMATION_TO_BASKET,
    REMOVE_INFORMATION_FROM_BASKET,
    CLEAN_BASKET,
    FETCH_BASKET_SUCCESS,
    BASKET_TO_ORDER_FAILURE,
    BASKET_TO_ORDER
} from 'actionTypes'

import {getInformationById} from 'selectors'

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_BASKET_SUCCESS:
            return payload;
        case ADD_INFORMATION_TO_BASKET:
            // if (getInformationById(state, payload).amount === 1){
            // }
            const data = {
                partId: payload,
                amount: 1
            };
            $.ajax({
                type: 'POST',
                url: '/api/card/add',
                data: data
            });
            return R.append(payload, state);
        case REMOVE_INFORMATION_FROM_BASKET:
            const data_ = {
                partId: payload
            };
            $.ajax({
                type: 'POST',
                url: '/api/card/remove',
                data: data_
            });
            return R.without(R.of(payload), state);
        case BASKET_TO_ORDER_FAILURE:
            console.log("Payload, fail");
            console.dir(payload);
            document.getElementById('info').textContent = 'На складе нt хватат товара: '+payload.response.text;
            return state;
        case BASKET_TO_ORDER:
            console.log("payload");
            console.dir(payload);
            document.getElementById('info').textContent = 'Заказ успешно оформлен. <br/> Номер Вашего заказа: '+
                payload + '<br/> Для оформления заказа воспользуйтесь ботом <a>https://t.me/RoboGitSellerBot</a>.' +
                '<br/>Напишите боту /pay ' + payload;
        case CLEAN_BASKET:
            $.ajax({
                type: 'POST',
                url: '/api/card/clean',
            });
            return [];
        default:
            return state;
    }
}
