import R from 'ramda'

import {
    FETCH_INFORMATION_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {
    id: null
};

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_INFORMATION_BY_ID_SUCCESS:
            console.dir(payload);
            return R.merge(state, {
                id: R.prop('id', payload)
            });
        default:
            return state;
    }
}
